// Cycle body classes jQuery script

$(function() {
  var $target = $("body");
  var classes = ['image1', 'image2', 'image3'];
  var current = 0;

  setInterval(function() {
    $target.removeClass(classes[current]);
    current = (current+1)%classes.length;
    $target.addClass(classes[current]);
  }, 10000); // Timer in ms
});

// Hide URL Bar for iOS
// http://davidwalsh.name/hide-address-bar

window.addEventListener("load",function() {
  // Set a timeout...
  setTimeout(function(){
    // Hide the address bar!
    window.scrollTo(0, 1);
  }, 0);
});

